import styled from "styled-components";

export const Image = styled.img`
    width: 100%;
    height: 100%;
    max-width: 720px;
    transition: all 0.3s;
    object-fit: cover;
    border-radius: 20px;
    animation: animateMovieThumb 0.5s;

    :hover {
        opacity: 0.8;
    }

    @keyframes animateMovieThumb {
        from {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }
`;
